import React, { useState, useEffect, useRef } from 'react';
import axios from "axios"
import logo from './logo.svg';
import './App.css';
import { Draw } from './Draw';
function App() {
  const deckId = useRef()

  const [newDeck, setNewDeck] = useState([]);
  const [isGameOver, setIsGameOver] = useState(false);
  const [click, setClick] = useState(null);

  useEffect(() => {
    axios.get('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1').then((res) => {
      deckId.current = (res.data.deck_id);
    })
  }, []);

  const draw = () => {
    const interval = setInterval(() => {
      const url = `https://deckofcardsapi.com/api/deck/${deckId.current}/draw/?count=1`
      axios.get(url).then((res) => {
        console.log('hello')
        if (res.data.remaining < 0) {
          setIsGameOver(true);
          debugger
          console.log(interval)
          clearInterval(interval)
          alert("GAME OVER")
          return
        }
        const newCard = res.data.cards[0];
        setNewDeck(prev => [...prev, newCard]);
      })
    }, 100);
  };

  useEffect(() => {
    if (isGameOver) {
      console.log('clearing interval')

    }
  }, [isGameOver])

  useEffect(() => {
    // draw();
    // return () => { clearInterval(draw())} 
  }, [])

  let count = 0


  return (
    <div className="App">
      {console.log("how many times will this render")}
      {!isGameOver ? <h2> START </h2> : <h2>game Over</h2>}

      <button onClick={draw}> draw </button>
      {newDeck.map((card) => {
        return (

          <p>
            {console.log(card.code, count++)} {card.code}</p>
        )
      })}
    </div>
  );
}

export default App;
