import React, { useState, useEffect, useRef } from 'react';
import axios from "axios"
import logo from './logo.svg';
import './App.css';
import { Draw } from './Draw';
function App() {
  const deckId = useRef()

  const [newDeck, setNewDeck] = useState([])
  const [isGameOver, setIsGameOver] = useState(false)

  useEffect(() => {
    axios.get('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1').then((res) => {
      deckId.current = (res.data.deck_id);
    })
  }, []);

  const draw = () => {
    const intervalId = setInterval(() => {
      const url = `https://deckofcardsapi.com/api/deck/${deckId.current}/draw/?count=1`
      axios.get(url).then((res) => {
        if (res.data.remaining === 0) {
          setIsGameOver(true);
          clearInterval(intervalId);
        };
        const newCard = res.data.cards[0];
        setNewDeck(prev => [...prev, newCard]);
      })
    }, 100)
  };

  let count = 0

const stopdraw = () => {
    const intervalId = draw();
    return  clearInterval(intervalId);
  }; 

  return (
    <div className="App">

      {!isGameOver ? <h2> START </h2> : <h2>game Over</h2>} 

      <button onClick={draw}> draw </button>
      {newDeck.map((card) => {
        return (

          <p>
           {console.log(card.code, count++)} {card.code ? card.code : <h1>all done</h1>}</p>
        )
      })}
    </div>
  );
}

export default App;
